var arr = [];
$('.cate_menu .cate_menu_item').each(function (index, obj) {
  var name = $(obj).find('a').eq(0).text();
  arr.push({
    label: name,
    value: name
  });
});
$('#J_popCtn .cate_part_col1').each(function (index, obj) {
  var children = arr[index].children = [];
  $(obj).find('.cate_detail_item').each(function (index1, obj1) {
    var l = $(obj1).find('.cate_detail_tit a').text().replace('ÓòÅ', '');
    children.push({
      label: l,
      value: l,
      children: function () {
        var a = [];
        $(obj1).find('.cate_detail_con a').each(function (index2, obj2) {
          var n = $(obj2).text();
          a.push({
            label: n,
            value: n
          });
        });
        return a;
      }()
    });
  });
});