// src/hasCoreImportDeclaration.ts
import { types as t } from "@babel/core";
function hasCoreImportDeclaration(path) {
  const importDeclarations = path.get("body").filter((node) => t.isImportDeclaration(node.node));
  return importDeclarations.some(
    (importDeclaration) => t.isImportDeclaration(importDeclaration.node) && importDeclaration.node.source.value === "@kuma-ui/core"
  );
}

export {
  hasCoreImportDeclaration
};
