// src/components/Spacer/handler.ts
import { toCssUnit } from "@kuma-ui/system";
var defaultSpacerTag = "div";
var spacerSpecificProps = [
  "horizontal",
  "size"
];
var isSpacerProps = (propName) => {
  return spacerSpecificProps.some((k) => k === propName);
};
var spacerDefaultProps = {};
var spacerHandler = (props) => {
  if (!props.horizontal && !props.size)
    return {};
  const px = typeof props.size === "undefined" ? "0px" : toCssUnit("width", props.size);
  return props.horizontal ? {
    width: px,
    height: "auto",
    display: "inline-block",
    flexShrink: 0
  } : { width: "auto", height: px, display: "block", flexShrink: 0 };
};
export {
  defaultSpacerTag,
  isSpacerProps,
  spacerDefaultProps,
  spacerHandler
};
